import React, { useRef } from "react";
import { useReactToPrint } from "react-to-print";

import badge from "../imgs/badge.jpeg"

const Registration = () => {
  const componentRef = useRef();
  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
  });

  return (
    <div className="container container-fluid p-5">
      <div className="row">
        <div className="col col-5 mr-5">
          <div className="row p-3">
            <h5>REGISTRATION</h5>
          </div>
          <div className="row p-3">
            <input type="text" className="form-control" placeholder="Full Name" />
          </div>
          <div className="row p-3">
            <input type="text" className="form-control" placeholder="Position" />
          </div>
          <div className="row p-3">
            <input type="text" className="form-control" placeholder="Company" />
          </div>
          <div className="row p-3">
            <button type="submit" className="btn btn-success" onClick={handlePrint}> Register & Print </button>
          </div>
        </div>
        <div className="col p-3">
          <h5>PREVIEW</h5>

          <div style={{ backgroundImage: `url(${badge})`, backgroundSize: "80%", backgroundRepeat: "no-repeat" }}>
            <ComponentToPrint ref={componentRef} />
          </div>
        </div>
      </div>
    </div >
  )
}

class ComponentToPrint extends React.Component {
  render() {
    return (
      <div style={{ height: "50rem", width: "35rem" }} className="col">
        <div style={{
          fontSize: "2.5rem", color: "#000", wordWrap: "true",
          textAlign: "center", paddingTop: "13rem", fontWeight: "bold",
          justifyContent: "center", position: "absolute", width: "30rem"
        }}>
          Ann Katherine Mantele asfjkahsf
        </div>
        <div style={{
          fontSize: "1.75rem", color: "#000", wordWrap: "true",
          textAlign: "center", paddingTop: "20rem", fontWeight: "bold"
        }}>
          Programmer
        </div>
        <div style={{
          fontSize: "1.75rem", color: "#000", wordWrap: "true",
          textAlign: "center", paddingTop: "0.5rem", fontWeight: "bold"
        }}>
          Creative Space Dubai
        </div>
      </div>
    );
  }
}

export default Registration
